<?php$this->showView('device_details');?><script type="text/javascript">smallMap();prepareUI();for (var i in deviceMarkers){	if (i != '<?php echo $device->id; ?>')	{		deviceMarkers[i].setVisible(false);	}	else	{		deviceMarkers[i].setDraggable(true);		var map = initializeGoogleMapsAPI();		map.setCenter(deviceMarkers[i].getPosition());	}}jQuery(".submit").click(function(){	if (validate('.left') && validatePowers())	{		jQuery.post("index.php", jQuery("#form").serialize()+"&group="+jQuery('select[name="group"]').val(),		function(data)		{			jQuery(".left").html(data);			fullMap();		});		if (deviceMarkers['<?php echo $device->id; ?>'] != undefined)		{			deviceMarkers['<?php echo $device->id; ?>'].setDraggable(false);		}	}});jQuery(".back").click(function(){	reloadDevices();	if (deviceMarkers['<?php echo $device->id; ?>'] != undefined)	{		deviceMarkers['<?php echo $device->id; ?>'].setDraggable(false);		deviceMarkers['<?php echo $device->id; ?>'].setPosition(new google.maps.LatLng(<?php echo $device->lat.', '.$device->lng; ?>));	}		fullMap();});</script>