<form action="index.php" method="post" id="form"><input type="hidden" name="content" value="UserAdmin" /><input type="hidden" name="action" value="save" /><input type="hidden" name="id" value="<?php echo $user->id; ?>"/><table class="user_edit">	<tr class="ui-widget-header">		<th colspan="2"><?php echo $lang['User info']; ?></th>	</tr>	<tr class="line">		<th><?php echo $lang['Furstname']; ?></th>		<td><input class="textbox" type="text" name="first_name" value="<?php echo $user->first_name; ?>"/></td>	</tr>	<tr class="line">		<th><?php echo $lang['Family']; ?></th>		<td><input class="textbox required" type="text" name="last_name" value="<?php echo $user->last_name; ?>"/></td>	</tr>	<tr class="line">		<th><?php echo $lang['Access']; ?></th>		<td>		<?php 		echo '<select name="role_id">';		while ($role = $roles->fetch_object())		{			echo '<option ';			if ($role->id == $user->role_id)			{				echo 'selected="selected" ';			}			echo 'value="'.$role->id.'">'.$lang[$role->role_name].'</option>';		}		echo '</select>';		?>		</td>	</tr>	<tr class="line">		<th><?php echo $lang['Username']; ?></th>		<td><input class="textbox required" type="text" name="user_name" <?php if ($user->id) echo 'readonly="readonly"'; ?> value="<?php echo $user->user_name; ?>"/></td>	</tr>	<tr class="line">		<th><?php echo $lang['Email']; ?></th>		<td><input class="textbox" type="text" name="e_mail" value="<?php echo $user->e_mail; ?>"/></td>	</tr></table><div class="ui-widget-header" style="clear: both; padding: 5px 0;">	<a class="submit"><?php echo $lang['Save']; ?></a>	<a class="cancel"><?php echo $lang['Cancel']; ?></a></div><script type="text/javascript">prepareUI();jQuery(".submit").click(function(){	if (validate('.right'))	{		jQuery.get("index.php",		{			content: "UserAdmin",			action: "checkUsername",			user_name: jQuery('input[name="user_name"]').val(),			id: '<?php echo $user->id; ?>'		}, function(data)		{			if (jQuery.trim(data) == 'OK')			{				jQuery("#form").submit();			}			else			{				jQuery('input[name="user_name"]').addClass('invalid1');			}		});	}});jQuery(".cancel").click(function(){	jQuery(".right").html('');});</script></form>